import React, { useCallback, useContext, useEffect, useState } from "react";
import axios from "axios";
import AppContext from "../AppContext/AppContext";

const Banner = () => {
  const { state, dispatch } = useContext(AppContext);
  const { products, user } = state;
  console.log(products);
  const getAllProducts = useCallback(async () => {
    try {
      const options = {
        method: "GET",
        url: "/api/v1/product",
      };
      const response = await axios(options);
      const products = response.data.data.products;
      dispatch({ type: "GET_ALL_PRODUCTS", payload: products });
    } catch (error) {
      // console.log(error);
    }
  }, [dispatch]);
  useEffect(() => {
    getAllProducts();
  }, [getAllProducts]);
  return (
    <section className="banner h-[500px] page-container mb-10">
      <div className="w-full h-full rounded-lg relative">
        <div className="overlay absolute inset-0 bg-gradient-to-t from-[rgba(0,0,0,0.5)] to-[rgba(0,0,0,0.5)] rounded-lg"></div>
        <img
          src="https://images.unsplash.com/photo-1507842217343-583bb7270b66?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1590&q=80"
          alt=""
          className="w-full h-full object-cover rounded-lg"
        />
        <div className="absolute left-5 bottom-20 w-full text-white">
          <h2 className="font-bold text-3xl mb-3">
            A book is a gift you can open again and again
          </h2>
          <button className="py-3 px-6 rounded-lg bg-primary text-white font-bold">
            Mua Ngay{" "}
          </button>
        </div>
      </div>
    </section>
  );
};

export default Banner;
