import React, { useContext, useState } from "react";
import axios from "axios";
import AppContext from "./../components/AppContext/AppContext";
import { Navigate } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import setupProxy from "../setupProxy";

const SignInPage = () => {
  const { dispatch } = useContext(AppContext);
  const [userInput, setUserInput] = useState({ email: "", password: "" });
  const [errorMessage, setErrorMessage] = useState(null);
  const navigate = useNavigate();
  const onChangeHandle = (e) => {
    setUserInput({ ...userInput, [e.target.name]: e.target.value });
  };
  const onSubmitHandle = async (e) => {
    try {
      e.preventDefault();
      const options = {
        method: "POST",
        url: "http://localhost:6969/api/v1/auth/login",
        data: userInput,
      };
      const response = await axios(options);
      const { token, username } = response.data.data;
      localStorage.setItem("token", token);
      dispatch({ type: "CURRENT_USER", payload: { username } });
      navigate("/");
    } catch (error) {
      setErrorMessage(error.response.data.message);
    }
  };
  return (
    <div className="flex justify-center h-[800px] bg-pink-300">
      <div className="flex-col">
        <h1 className="font-bold text-4xl mt-8">Login</h1>
        <form action="" className="mt-20" onSubmit={onSubmitHandle}>
          <div className="flex flex-col gap-y-4">
            {errorMessage && (
              <div className="error-message">Error:{errorMessage}</div>
            )}
            <label htmlFor="name" className="font-bold">
              Email
            </label>
            <input
              type="email"
              name="email"
              className="w-[500px] bg-slate-100 p-3 rounded-lg focus:bg-white outline-cyan-500"
              placeholder="Nhập email"
              id=""
              required
              value={userInput.email}
              onChange={onChangeHandle}
            />
            <label htmlFor="name" className="font-bold">
              Mật khẩu
            </label>
            <input
              type="password"
              name="password"
              className="w-[500px] bg-slate-100 p-3 rounded-lg  focus:bg-white outline-cyan-500"
              placeholder="Nhập mật khẩu"
              id=""
              required
              value={userInput.password}
              onChange={onChangeHandle}
            />
            <button
              className="rounded-full border border-slate-300 hover:border-pink-700"
              type="submit"
            >
              Đăng nhập
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SignInPage;
