const mongoose = require("mongoose");

const productSchema = new mongoose.Schema(
  {
    name: {
      type: String,
      trim: true,
      required: [true, " Tên sản phẩm không được bỏ trống !!! "],
    },

    slug: {
      type: String,
      unique: true,
    },

    price: {
      type: Number,
      required: [true, " Giá sản phẩm không được bỏ trống !!!"],
    },

    description: {
      type: String,
      required: [true, " Mô tả sản phẩm không được bỏ trống !!! "],
    },

    offer: {
      type: Number,
    },
    productImage: [{ img: { type: String } }],

    reviews: [
      { userId: mongoose.Schema.Types.ObjectId, ref: "User", review: String },
    ],
    catagory: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "Category",
    },
    createBy: { type: mongoose.Schema.Types.ObjectId, ref: "User" },
    updateAt: Date,
  },
  { timestamps: true }
);

module.exports = mongoose.model("Product", productSchema);
