import React from "react";

const productCard = () => {
  return (
    <div className="product-card rounded-lg p-3 bg-slate-800 text-white">
      <img
        src="https://salt.tikicdn.com/cache/200x200/ts/product/77/8d/16/ec182c0fcc1431070504e6108b5a0f2b.jpg.webp"
        alt=""
        className="w-full h-[250px] object-cover rounded-lg mb-5"
      />
      <h3 className=" text-xl font-bold mb-3">Tâm lý học về tiền</h3>
      <div className="flex items-center justify-between text-sm opacity-50 mb-5">
        <span>100.000đ</span>
        <span>4.8</span>
      </div>
      <button className="py-3 px-6 rounded-lg capitalize bg-primary w-full">
        Mua ngay
      </button>
    </div>
  );
};

export default productCard;
