commits.content:
.header{
    display: flex;
    align-items: center;
    justify-content: center;
    column-gap: 10px;
    margin-bottom: 10px;
    font-size: 20px;
}

.banner{
    background-color: #fff;
    height: 400px;
    max-width: 800px;
    margin-left: auto;
    margin-right: auto;
    border-radius: 12px;
    position: relative;
}
.main-banner{
    width:100%;
    height:100%;
}
.imgae{
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 12px;
}
.title-banner{
    position: absolute;
    left: 5px;
    bottom: 150px;
    width: 100%;
    color: #fff;
}
.title{
    font-weight: bold;
    font-size: 30px;
    margin-left:10px;
}
